mapper.update:
update shop_car set selected = #{selected}
where

     commodity_id = #{commodityId}

and user_id = #{userId}

mapper.delete:
delete from shop_car
where  commodity_id in

    #{item}

and user_id=#{userId}

delete from shop_car
where commodity_id in

    #{item.commodityId}

and user_id=#{userId}
